import React, { Component } from 'react';


export const ThisContext = React.createContext()

class ThisContextProvider extends Component {
    constructor(){
        super()
        this.state ={
            lightMode : true,
            light : {modeFont:"#5c5c5c", backgroundChoice:"#fffff", buttonColor:"#a3a3a3"},
            dark : {modeFont:"#fffff", backgroundChoice: "#5c5c5c", buttonColor: "#5c5858" }
        }
    }
    render() { 
        return ( 
            <div>
               <ThisContext.Provider value="{{...this.state}}">
                   {this.props.children}
               </ThisContext.Provider>
            </div>
         );
    }
}
 
export default ThisContextProvider;
