import React, { Component } from 'react';


class DisplayGame extends Component {
    constructor(){
        super()
        this.state ={
            stakes : 8,
            unit : 40.02,
            bet_price: 0,
            winning_average : 20
        }
    }
    handleStake(){
        this.setState({
            stakes : this.state.stakes *4
        })
    }
    handlePrice(){
        this.setState({
            bet_price : this.state.bet_price + this.state.unit
        })
    }
    xxPrice(){
        this.setState({
            bet_price : this.state.bet_price *0
        })
    }
    xxStake(){
        this.setState({
            stakes : this.state.stakes *0 + 8
        })
    }
    render() { 
        return ( 
            <div>
                <button>{this.state.stakes}</button>
                <button>{this.state.unit}</button>
                <button>{this.state.bet_price}</button>
                <button>{this.state.winning_average}</button>
                <div>  
                    <button onClick={this.handleStake}>+stakes</button>
                    <button onClick={this.handlePrice}>+price</button>
                    <button onClick={this.xxStake}>-stakes</button>
                    <button onClick={this.xxPrice}>-price</button>
                </div>
            </div>
            
         );
    }
}
 
export default DisplayGame;
