import React, { Component } from 'react';


export const ThisContext = React.createContext()

class ThisContextProvider extends Component {
    constructor(){
        super()
        this.state ={
            lightMode : true,
            light : {modeFont:"#000000", background:"#ffffff", buttonColor:"#23395d",},
            dark : {modeFont:"#ffffff", background: "#152238", buttonColor: "#63C5DA" }
        }
        this.changeTheme = this.changeTheme.bind(this)
    }
    changeTheme = () =>{
        this.setState({
            lightMode : !this.state.lightMode
        })
    }
    render() { 
        return ( 
            <div>
               <ThisContext.Provider value={{...this.state, changeTheme:this.changeTheme}}>
                   {this.props.children}
               </ThisContext.Provider>
            </div>
         );
    }
}
 
export default ThisContextProvider;
